<script>
export default {
  name: 'STransition',
  props: {
    reverse: {
      type: Boolean,
      default: false
    },
    once: {
      type: Boolean,
      default: false
    }
  },
  data() {
    return {
      tl: null,
    }
  },
  watch: {
    reverse() {
      this.start()
    }
  },
  mounted() {
    const self = this
    const targets = this.$refs.transitionContainer.getElementsByClassName('img')
    const svgs = this.$refs.transitionContainer.getElementsByClassName('svgSvg')
    this.gsap.config({
      force3D: true
    })
    this.tl = this.gsap.timeline({
      paused: true
    })
    this.tl.to(targets, {
      // eslint-disable-next-line object-shorthand
      y: function(i) {
        return '100%'
      },
        // eslint-disable-next-line object-shorthand
        duration: function(i) {
          return 2.6 - i * 0.3
        },
        ease: 'cubic',
        // eslint-disable-next-line object-shorthand
        stagger: function(i) {
          return 0.3 * (i-1)
        }
      })
    this.tl.to(svgs, {
      ease: 'cubic',
      scaleY: 2,
      transformOrigin: 'top',
      // eslint-disable-next-line object-shorthand
      duration: function(i) {
        return 1.6 - (i - 1) * 0.3
      },
      // eslint-disable-next-line object-shorthand
      stagger: function(i) {
        return 0.3 * (i-1)
      }
    }, '<')
    this.tl.to(this.$refs.transitionContainer, {
      opacity: self.once ? 0 : 1,
      duration() {
        return self.once ? 1 : 0
      }
    })
    this.start()
  },
  methods: {
    start() {
      this.reverse ? this.tl.reverse(this.once ? 0 : '') : this.tl.play()
    }
  }
}
</script>
